import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [messages, setMessages] = useState([]);
  const [socket, setSocket] = useState(null);

  useEffect(() => {
    const ws = new WebSocket('ws://161.35.192.142:8000/ws');

    ws.onopen = () => {
      console.log('Connected to WebSocket');
      setSocket(ws);
    };

    ws.onmessage = (event) => {
      const data = JSON.parse(event.data);
      setMessages(prev => [...prev, data]);
    };

    ws.onerror = (error) => {
      console.error('WebSocket error:', error);
    };

    ws.onclose = () => {
      console.log('Disconnected from WebSocket');
    };

    return () => {
      ws.close();
    };
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        <h1>CrewAI Agent Monitor</h1>
      </header>
      <main>
        <div className="agent-activities">
          {messages.map((msg, index) => (
            <div key={index} className="activity-card">
              <div className="activity-header">
                <span className="agent-name">{msg.agent}</span>
                <span className="timestamp">{new Date(msg.timestamp).toLocaleTimeString()}</span>
              </div>
              <div className="task-type">{msg.task}</div>
              <div className="output">{msg.output}</div>
            </div>
          ))}
        </div>
      </main>
    </div>
  );
}

export default App;
