import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [messages, setMessages] = useState([]);
  const [socket, setSocket] = useState(null);
  const [connectionStatus, setConnectionStatus] = useState('Connecting...');

  useEffect(() => {
    const ws = new WebSocket('ws://161.35.192.142:8000/ws');

    ws.onopen = () => {
      console.log('Connected to WebSocket');
      setConnectionStatus('Connected to monitoring server');
      setSocket(ws);
    };

    ws.onmessage = (event) => {
      const data = JSON.parse(event.data);
      setMessages(prev => [...prev, data]);
      // Auto scroll to bottom
      const element = document.getElementById('message-container');
      if (element) {
        element.scrollTop = element.scrollHeight;
      }
    };

    ws.onerror = (error) => {
      console.error('WebSocket error:', error);
      setConnectionStatus('Error connecting to server');
    };

    ws.onclose = () => {
      console.log('Disconnected from WebSocket');
      setConnectionStatus('Disconnected from server');
    };

    return () => {
      ws.close();
    };
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        <h1>CrewAI Agent Monitor</h1>
        <div className={`connection-status ${connectionStatus.includes('Connected') ? 'connected' : 'disconnected'}`}>
          {connectionStatus}
        </div>
      </header>
      <main>
        <div id="message-container" className="agent-activities">
          {messages.length === 0 ? (
            <div className="no-activity">
              <p>Waiting for agent activities...</p>
              <p className="hint">Run your CrewAI script to see agent activities here</p>
            </div>
          ) : (
            messages.map((msg, index) => (
              <div key={index} className="activity-card">
                <div className="activity-header">
                  <span className="agent-name">{msg.agent}</span>
                  <span className="timestamp">{new Date(msg.timestamp).toLocaleTimeString()}</span>
                </div>
                <div className="task-type">{msg.task}</div>
                <div className="output">{msg.output}</div>
              </div>
            ))
          )}
        </div>
      </main>
    </div>
  );
}

export default App;
